import React, { useState } from 'react';
import {
  getWalletAddress,
  getAddressBalance,
  createDataTransaction,
  signAndSubmitTransaction,
  getTransactionData,
} from './Arweave';
import './Create.css';
import { Link, useHistory } from 'react-router-dom';

const Create = (props) => {
  const [confessions, setConfessions] = useState([]);
  const [text, setText] = useState('');
  const [bio, setBio] = useState('');
  const [conf, setConf] = useState('');
  const [files, setFiles] = useState([]);
  let history = useHistory();
  const { REACT_APP_KEY } = process.env;

  // const handleSubmit = (evt) => {
  //   evt.preventDefault();
  //   const newUser = { name: text, bio: bio, confession: conf };

  //   props.getData(newUser);
  //   setConfessions([...confessions, newUser]);
  // };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const newUser = { name: text, bio: bio, confession: conf };

    props.getData(newUser);
    setFiles([...confessions, newUser]);

    const dataFile = files[0];
    console.log(dataFile);

    const reader = new FileReader();

    reader.onload = async () => {
      const url = reader.result;

      let transaction = await createDataTransaction(
        url,
        JSON.parse(REACT_APP_KEY)
      );
      await signAndSubmitTransaction(transaction, JSON.parse(REACT_APP_KEY));

      history.push('/');
    };

    reader.readAsDataURL(dataFile);
  };

  return (
    <div className='Create'>
      <form onSubmit={handleSubmit}>
        <label>Enter Name</label>
        <br />
        <input
          type='text'
          id='name'
          name='text'
          onChange={(e) => setText(e.target.value)}
        />
        <br />
        <label>Enter Bio</label>
        <br />
        <textarea
          id='bio'
          name='bio'
          onChange={(e) => setBio(e.target.value)}
        ></textarea>
        <label>Enter Confession</label>
        <br />
        <textarea
          id='confession'
          name='conf'
          onChange={(e) => setConf(e.target.value)}
        ></textarea>
        <input id='submit' type='submit' />
      </form>
    </div>
  );
};

export default Create;
