import React, { useState, useEffect } from 'react';
import { queryData, getTransactionData } from './Arweave';

import './View.css';

const View = (props) => {
  useEffect(() => {
    const fetchData = async () => {
      try {
        let response = await queryData(JSON.parse(REACT_APP_KEY));
        console.log(response);

        let arr = [];

        for (var i = 0; i < response.length; i++) {
          let data = await getTransactionData(response[i]);
          console.log(data);

          let parseData = JSON.parse(data);

          arr.push(parseData);
        }
      } catch (e) {
        throw e;
      }
    };
    fetchData();
  }, []);

  return (
    <div>
      <h1>View Data</h1>
      {props.confessions.map((u) => {
        return (
          <div className='User-card'>
            <div className='User-avatar'>
              <img src={`https://robohash.org/${u.name}`} alt={u.name}></img>
            </div>
            <h3>{u.name}</h3>
            <h4>{u.bio}</h4>
            <div className='User-conf'>
              <p>{u.confession} </p>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default View;
